mapper.select:
SELECT cart.cart_code, cart.email, cart.book_number, cart.book_title, cart.sale_price, cart.cart_amount, cart.select_yn
FROM cart
WHERE cart.email = #{email}

SELECT book_number, book_title, sale_price
FROM product
WHERE book_number = #{book_number}

mapper.delete:
DELETE FROM cart
WHERE email = #{email}
AND cart_code IN

    #{cart_code}
